mapper.insert:
insert into
    category
    (
    member_id
    , category
    , created_at
    )
    values
    (
    #{writerId}
    , #{category}
    , #{createdAt}
    )

mapper.update:
update category
    set
        member_id = #{memberId}
        , category = #{category}
    where
        id = #{id}
