mapper.insert:
insert into memo
    (
        id
        , writer_id
        , memo
        , category
        , updated_at
    )
    values
    (
        #{id}
        , #{writerId}
        , #{memo}
        , #{category}
        , #{updateAt}
    )

mapper.update:
update memo
    set
        memo = #{memo}
        , category = #{category}
        , updated_at = #{updateAt}
    where
        id = #{id}
