mapper.select:
select id , member_id , memo , category , created_at , updated_at from memo
and memo like concat('%', #{memo} ,'%')
and id = #{id}

mapper.insert:
insert into memo
    (
        member_id
        , memo
        , category
    )
    values
    (
        #{memberId}
        , #{memo}
        , #{category}
    )

mapper.update:
update memo
    set
        memo = #{memo}
        , category = #{category}
        , updated_at = #{updateAt}
    where
        id = #{id}
