mapper.insert:
insert into memo
    (
        id
        , member_id
        , memo
        , category
    )
    values
    (
        #{id}
        , #{memberId}
        , #{memo}
        , #{category}
    )

mapper.update:
update memo
    set
        memo = #{memo}
        , category = #{category}
        , updated_at = #{updateAt}
    where
        id = #{id}
